// =========================
// AUDIO REACTIVITY TEST
// =========================
// Visual debug display showing all audio parameters in real-time

// ────────── CONFIG ──────────
const BAR_HEIGHT = 60;         // Height of each meter bar
const BAR_PADDING = 20;        // Space between bars
const MARGIN = 40;             // Screen margin
const SHOW_VALUES = true;      // Display numeric values
const SHOW_BANDS = true;       // Show frequency band visualization

// =========================
// RENDER
// =========================

// Gradient background
let bgGrad = ctx.createLinearGradient(0, 0, 0, height);
bgGrad.addColorStop(0, 20, 25, 35, 255);
bgGrad.addColorStop(1, 10, 15, 25, 255);
ctx.fillStyleGradient(bgGrad);
ctx.fillRect(0, 0, width, height);

let yPos = MARGIN;

// ========== BASS METER ==========
ctx.setFont('20px Arial');
ctx.textAlign('left');
ctx.fillStyle(255, 255, 255, 255);
ctx.fillText('BASS', MARGIN, yPos + 20);

let bassWidth = (width - MARGIN * 2 - 100) * audio.Bass;
ctx.fillStyle(255, 50, 80, 255);
ctx.fillRect(MARGIN + 100, yPos, bassWidth, BAR_HEIGHT);

// Outline
ctx.strokeStyle(255, 255, 255, 150);
ctx.lineWidth(2);
ctx.strokeRect(MARGIN + 100, yPos, width - MARGIN * 2 - 100, BAR_HEIGHT);

// Value text
if (SHOW_VALUES) {
  ctx.textAlign('right');
  ctx.fillStyle(255, 255, 255, 255);
  ctx.fillText((audio.Bass * 100).toFixed(1) + '%', width - MARGIN, yPos + 20);
}

// Visual pulse circle
let bassSize = 30 + audio.Bass * 40;
ctx.shadowBlur(20);
ctx.shadowColor(255, 50, 80, 255);
ctx.fillStyle(255, 50, 80, 255);
ctx.fillCircle(width - MARGIN - 150, yPos + BAR_HEIGHT / 2, bassSize);
ctx.shadowBlur(0);

yPos += BAR_HEIGHT + BAR_PADDING;

// ========== MIDRANGE METER ==========
ctx.textAlign('left');
ctx.fillStyle(255, 255, 255, 255);
ctx.fillText('MIDRANGE', MARGIN, yPos + 20);

let midWidth = (width - MARGIN * 2 - 100) * audio.Midrange;
ctx.fillStyle(80, 200, 255, 255);
ctx.fillRect(MARGIN + 100, yPos, midWidth, BAR_HEIGHT);

ctx.strokeStyle(255, 255, 255, 150);
ctx.lineWidth(2);
ctx.strokeRect(MARGIN + 100, yPos, width - MARGIN * 2 - 100, BAR_HEIGHT);

if (SHOW_VALUES) {
  ctx.textAlign('right');
  ctx.fillStyle(255, 255, 255, 255);
  ctx.fillText((audio.Midrange * 100).toFixed(1) + '%', width - MARGIN, yPos + 20);
}

let midSize = 30 + audio.Midrange * 40;
ctx.shadowBlur(20);
ctx.shadowColor(80, 200, 255, 255);
ctx.fillStyle(80, 200, 255, 255);
ctx.fillCircle(width - MARGIN - 150, yPos + BAR_HEIGHT / 2, midSize);
ctx.shadowBlur(0);

yPos += BAR_HEIGHT + BAR_PADDING;

// ========== TREBLE METER ==========
ctx.textAlign('left');
ctx.fillStyle(255, 255, 255, 255);
ctx.fillText('TREBLE', MARGIN, yPos + 20);

let trebleWidth = (width - MARGIN * 2 - 100) * audio.Treble;
ctx.fillStyle(255, 220, 50, 255);
ctx.fillRect(MARGIN + 100, yPos, trebleWidth, BAR_HEIGHT);

ctx.strokeStyle(255, 255, 255, 150);
ctx.lineWidth(2);
ctx.strokeRect(MARGIN + 100, yPos, width - MARGIN * 2 - 100, BAR_HEIGHT);

if (SHOW_VALUES) {
  ctx.textAlign('right');
  ctx.fillStyle(255, 255, 255, 255);
  ctx.fillText((audio.Treble * 100).toFixed(1) + '%', width - MARGIN, yPos + 20);
}

let trebleSize = 30 + audio.Treble * 40;
ctx.shadowBlur(20);
ctx.shadowColor(255, 220, 50, 255);
ctx.fillStyle(255, 220, 50, 255);
ctx.fillCircle(width - MARGIN - 150, yPos + BAR_HEIGHT / 2, trebleSize);
ctx.shadowBlur(0);

yPos += BAR_HEIGHT + BAR_PADDING;

// ========== VOLUME METER ==========
ctx.textAlign('left');
ctx.fillStyle(255, 255, 255, 255);
ctx.fillText('VOLUME', MARGIN, yPos + 20);

let volumeWidth = (width - MARGIN * 2 - 100) * audio.Volume;
ctx.fillStyle(150, 255, 150, 255);
ctx.fillRect(MARGIN + 100, yPos, volumeWidth, BAR_HEIGHT);

ctx.strokeStyle(255, 255, 255, 150);
ctx.lineWidth(2);
ctx.strokeRect(MARGIN + 100, yPos, width - MARGIN * 2 - 100, BAR_HEIGHT);

if (SHOW_VALUES) {
  ctx.textAlign('right');
  ctx.fillStyle(255, 255, 255, 255);
  ctx.fillText((audio.Volume * 100).toFixed(1) + '%', width - MARGIN, yPos + 20);
}

let volumeSize = 30 + audio.Volume * 40;
ctx.shadowBlur(20);
ctx.shadowColor(150, 255, 150, 255);
ctx.fillStyle(150, 255, 150, 255);
ctx.fillCircle(width - MARGIN - 150, yPos + BAR_HEIGHT / 2, volumeSize);
ctx.shadowBlur(0);

yPos += BAR_HEIGHT + BAR_PADDING * 2;

// ========== FREQUENCY BANDS (0-31) ==========
if (SHOW_BANDS) {
  ctx.textAlign('left');
  ctx.fillStyle(255, 255, 255, 255);
  ctx.fillText('FREQUENCY BANDS (0-31)', MARGIN, yPos + 20);

  yPos += 40;

  let bandWidth = (width - MARGIN * 2) / 32;
  let maxBandHeight = height - yPos - MARGIN;

  for (let i = 0; i < 32; i++) {
    let bandValue = audio.GetBand(i);
    let barHeight = bandValue * maxBandHeight;

    // Color gradient from red (bass) to yellow (mid) to cyan (treble)
    let hue = i / 32;
    let rgb = ctx.hslToRgb(hue * 0.6, 1, 0.6);

    ctx.fillStyle(rgb.r, rgb.g, rgb.b, 255);
    ctx.fillRect(
      MARGIN + i * bandWidth, 
      height - MARGIN - barHeight, 
      bandWidth - 2, 
      barHeight
    );

    // Glow effect
    ctx.shadowBlur(10);
    ctx.shadowColor(rgb.r, rgb.g, rgb.b, 200);
    ctx.fillRect(
      MARGIN + i * bandWidth, 
      height - MARGIN - barHeight, 
      bandWidth - 2, 
      barHeight
    );
  }
  ctx.shadowBlur(0);

  // Band numbers
  ctx.fillStyle(255, 255, 255, 150);
  ctx.setFont('10px Arial');
  ctx.textAlign('center');
  for (let i = 0; i < 32; i += 4) {
    ctx.fillText(i.toString(), MARGIN + i * bandWidth + bandWidth / 2, height - MARGIN + 15);
  }
}

// ========== AUDIO STATUS ==========
ctx.textAlign('center');
ctx.setFont('16px Arial');
if (audio.IsEnabled) {
  ctx.fillStyle(150, 255, 150, 255);
  ctx.fillText('🎵 AUDIO ENABLED', width / 2, 25);
} else {
  ctx.fillStyle(255, 100, 100, 255);
  ctx.fillText('🔇 AUDIO DISABLED', width / 2, 25);
}
