// ==================== CONFIG ====================
const config = {
    cellSize: 40,
    overlap: 1.5, // How much cells overlap (1.5 = 150% size)
    color1: {r: 0, g: 100, b: 255}, // Cell Color A
    color2: {r: 0, g: 255, b: 150}  // Cell Color B
};

// ==================== DRAW ====================
// Background (Deep substrate - barely visible due to overlap)
ctx.fillStyle(0, 20, 40, 255);
ctx.fillRect(0, 0, width, height);

let cols = Math.ceil(width / config.cellSize) + 1;
let rows = Math.ceil(height / config.cellSize) + 1;

// Audio affects cell "pressure" (size)
let pressure = audio.IsEnabled ? audio.Bass * 20 : 0;

for (let y = 0; y < rows; y++) {
    for (let x = 0; x < cols; x++) {
        // Base Grid Position
        let gx = x * config.cellSize;
        let gy = y * config.cellSize;

        // Offset logic (wobbly grid)
        // We use Math.sin based on x, y, and time to shift centers organically
        let offsetX = Math.sin(x * 0.5 + time) * (config.cellSize * 0.3);
        let offsetY = Math.cos(y * 0.5 + time) * (config.cellSize * 0.3);

        let cx = gx + offsetX;
        let cy = gy + offsetY;

        // Size variation
        let pulse = Math.sin(x + y + time * 3) * 5;
        let r = (config.cellSize * config.overlap / 2) + pulse + pressure;

        // Color variation (Checkerboard mix)
        let mix = (Math.sin(x * 0.2) + Math.cos(y * 0.2) + 2) / 4; // 0..1

        let cr = config.color1.r * mix + config.color2.r * (1-mix);
        let cg = config.color1.g * mix + config.color2.g * (1-mix);
        let cb = config.color1.b * mix + config.color2.b * (1-mix);

        // Highlight (Nucleus)
        ctx.fillStyle(255, 255, 255, 100);
        ctx.fillCircle(cx - r*0.3, cy - r*0.3, r * 0.2);
    }
}
