// ============================================
// CONFIGURATION
// ============================================
const config = {
    // Grid Settings
    cellSize: 20,              // Base size of each cell in pixels

    // Animation Settings
    waveFrequency: 0.05,       // How tight the wave pattern is (0.01 = loose, 0.1 = tight)
    waveSpeed: 1.0,            // Overall animation speed multiplier
    timeWarpX: 1.0,            // Horizontal wave speed multiplier
    timeWarpY: 1.3,            // Vertical wave speed multiplier
    sizeOscillation: 0.5,      // How much cells shrink/grow (0 = no change, 1 = full range)

    // Color Settings
    colorShiftSpeed: 0.1,      // How fast colors cycle through hue
    saturation: 1.0,           // Color saturation (0 = grayscale, 1 = vibrant)
    brightColor: 0.5,          // Brightness of primary cells (0 = black, 1 = white)
    darkColor: 0.3,            // Brightness of alternate cells
    hueOffset: 0.5             // Color difference between cell types (0.5 = opposite colors)
};

// ============================================
// ANIMATION CODE
// ============================================
ctx.clear(0, 0, 0);

const baseSize = config.cellSize;

for (let x = 0; x < width; x += baseSize) {
    for (let y = 0; y < height; y += baseSize) {
        // Oscillating size based on position and time
        let sizeModifier = Math.sin(x * config.waveFrequency + time * config.timeWarpX * config.waveSpeed) * 
                          Math.cos(y * config.waveFrequency + time * config.timeWarpY * config.waveSpeed) * 
                          config.sizeOscillation + 0.5;

        let cellSize = baseSize * sizeModifier;

        // Checkerboard pattern with color shift
        let isEven = ((x / baseSize) + (y / baseSize)) % 2;
        let hue = (time * config.colorShiftSpeed + (x + y) / (width + height)) % 1;

        if (isEven < 1) {
            let rgb = ctx.hslToRgb(hue, config.saturation, config.brightColor);
            ctx.fillStyle(rgb.r, rgb.g, rgb.b, 255);
        } else {
            let rgb = ctx.hslToRgb((hue + config.hueOffset) % 1, config.saturation, config.darkColor);
            ctx.fillStyle(rgb.r, rgb.g, rgb.b, 255);
        }

        ctx.fillRect(x, y, cellSize, cellSize);
    }
}
