// =========================
// CONFIG
// =========================
const RING_COUNT = 15;
const PULSE_SPEED = 1.5;
const LINE_WIDTH = 30;
const SATURATION = 0.9;
const BRIGHTNESS = 0.65;

// =========================
// RENDER
const bgColor = ctx.hslToRgb(0.15, 0.4, 0.9);
ctx.clear(bgColor.r, bgColor.g, bgColor.b);

const cx = width / 2;
const cy = height / 2;
const maxRadius = Math.max(width, height);
const pulse = Math.sin(time * PULSE_SPEED) * 0.2 + 1;

for (let i = 0; i < RING_COUNT; i++) {
    let radius = (i + 1) * (maxRadius / RING_COUNT) * pulse;
    let hue = (i / RING_COUNT + time * 0.1) % 1;
    let rgb = ctx.hslToRgb(hue, SATURATION, BRIGHTNESS);
    
    ctx.strokeStyle(rgb.r, rgb.g, rgb.b, 255);
    ctx.lineWidth(LINE_WIDTH);
    ctx.strokeCircle(cx, cy, radius);
}
