// ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
// ✨ CONFIGURATION
// ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
const config = {
  gridSize: 30,         // Size of grid cells
  waveZoom: 0.005,      // Scale of the noise pattern
  speed: 2.0,           // Speed of flow
  bg: [255, 0, 100],    // Hot Pink Background
  fg: [255, 255, 0]     // Yellow Dots
};

// ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
// 🎨 BACKGROUND
// ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
ctx.fillStyle(config.bg[0], config.bg[1], config.bg[2], 255);
ctx.fillRect(0, 0, width, height);

// ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
// 🔵 DOT GRID
// ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
const cols = Math.ceil(width / config.gridSize);
const rows = Math.ceil(height / config.gridSize);
const maxRadius = config.gridSize * 0.6; // Max dot size (allows overlap)

let t = time * config.speed;

// Audio affects global dot size
let beat = audio.IsEnabled ? (audio.Bass * 10) : 0;

ctx.fillStyle(config.fg[0], config.fg[1], config.fg[2], 255);

for (let y = 0; y < rows; y++) {
    for (let x = 0; x < cols; x++) {

        // Coordinates
        let px = (x * config.gridSize) + (config.gridSize/2);
        let py = (y * config.gridSize) + (config.gridSize/2);

        // Wave Calculation
        // Complex interference pattern
        let v1 = Math.sin(px * config.waveZoom + t);
        let v2 = Math.cos(py * config.waveZoom + t);
        let v3 = Math.sin((px+py) * config.waveZoom * 2 + t);

        // Normalize value (-3 to 3) -> (0.0 to 1.0)
        let val = (v1 + v2 + v3 + 3) / 6;

        // Calculate Radius based on wave value
        let r = (val * maxRadius) + beat;
        if (r < 0) r = 0;

        // Draw Dot
        ctx.beginPath();
        ctx.arc(px, py, r, 0, Math.PI * 2);
        ctx.fill();
    }
}

// ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
// 🖼️ VIGNETTE OVERLAY
// ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
// Darkens the corners for a poster look
// Since we don't have gradients, we can draw a thick stroke
ctx.lineWidth(100);
ctx.strokeStyle(0, 0, 0, 100); // Transparent black
ctx.strokeRect(-50, -50, width+100, height+100);
