// =========================
// CONFIG
// =========================
const STEP = 8;
const FIELD_SCALE = 0.03;
const FLOW_SPEED = 0.5;
const LINE_LENGTH = 15;
const COLOR_INTENSITY = 0.6;

// =========================
// RENDER
ctx.clear(0, 0, 10);

ctx.lineWidth(2);

for (let x = 0; x < width; x += STEP) {
    for (let y = 0; y < height; y += STEP) {
        // Calculate field direction
        let dx = x - width / 2;
        let dy = y - height / 2;
        let dist = Math.sqrt(dx*dx + dy*dy) + 1;

        let fieldAngle = Math.atan2(dy, dx) + 
                        Math.sin(dist * FIELD_SCALE + time * FLOW_SPEED) * 2;

        let x2 = x + Math.cos(fieldAngle) * LINE_LENGTH;
        let y2 = y + Math.sin(fieldAngle) * LINE_LENGTH;

        let hue = (fieldAngle / (Math.PI * 2) + time * 0.1) % 1;
        let rgb = ctx.hslToRgb(hue, 1, COLOR_INTENSITY);

        ctx.strokeStyle(rgb.r, rgb.g, rgb.b, 200);
        ctx.drawLine(x, y, x2, y2);
    }
}
