// ==================== CONFIG ====================
const config = {
    hexSize: 30,
    gap: 4,
    colorCenter: {h: 0.1, s: 1.0, l: 0.6}, // Orange
    colorEdge:   {h: 0.6, s: 1.0, l: 0.3}  // Blue
};

// ==================== DRAW ====================
// Background
ctx.fillStyle(10, 5, 15, 255);
ctx.fillRect(0, 0, width, height);

let r = config.hexSize;
let w = r * 2;
let h = Math.sqrt(3) * r;

// Grid dimensions
let cols = Math.ceil(width / (w * 0.75)) + 1;
let rows = Math.ceil(height / h) + 1;
let cx = width / 2;
let cy = height / 2;

// Audio
let bass = audio.IsEnabled ? audio.Bass : Math.sin(time)*0.5+0.5;

for (let col = -1; col < cols; col++) {
    for (let row = -1; row < rows; row++) {
        // Hexagon Center Logic
        let x = col * (w * 0.75);
        let y = row * h;
        if (col % 2 !== 0) y += h/2;

        // Distance from center of screen
        let dx = x - cx;
        let dy = y - cy;
        let dist = Math.sqrt(dx*dx + dy*dy);
        let maxDist = Math.max(width, height) / 2;

        // Calculate Scale
        // Pulse moves outward: sin(dist - time)
        let wave = Math.sin((dist * 0.01) - (time * 3));

        // Audio boosts the wave peaks
        let scale = 0.5 + (wave * 0.3) + (bass * 0.2);
        scale = Math.max(0.1, Math.min(1.0, scale));

        // Calculate Color (Gradient from center)
        let t = dist / maxDist;
        let hue = config.colorCenter.h + (config.colorEdge.h - config.colorCenter.h) * t;
        let rgb = ctx.hslToRgb(hue, 1.0, 0.5 * scale + 0.1);

        ctx.fillStyle(rgb.r, rgb.g, rgb.b, 255);

        // Draw Hex
        // Effective radius
        let effR = (r - config.gap) * scale;

        ctx.beginPath();
        for (let i = 0; i < 6; i++) {
            let angle = (Math.PI / 3) * i;
            ctx.lineTo(x + effR * Math.cos(angle), y + effR * Math.sin(angle));
        }
        ctx.fill();
    }
}
