// =========================
// CONFIG
// =========================
const BAND_COUNT = 30;
const WAVE_AMPLITUDE = 50;
const WAVE_SPEED = 1.0;
const SATURATION = 0.85;
const BRIGHTNESS = 0.6;

// =========================
// RENDER
ctx.clear(255, 255, 255);

const bandHeight = height / BAND_COUNT;

for (let i = 0; i < BAND_COUNT; i++) {
    let y = i * bandHeight;
    let wave = Math.sin(i * 0.5 + time * WAVE_SPEED) * WAVE_AMPLITUDE;

    let hue = (i / BAND_COUNT + time * 0.1) % 1;
    let rgb = ctx.hslToRgb(hue, SATURATION, BRIGHTNESS);

    ctx.fillStyle(rgb.r, rgb.g, rgb.b, 255);

    ctx.beginPath();
    ctx.moveTo(0, y);

    for (let x = 0; x <= width; x += 20) {
        let localWave = Math.sin(x * 0.01 + i + time) * wave;
        ctx.lineTo(x, y + localWave);
    }

    ctx.lineTo(width, height);
    ctx.lineTo(0, height);
    ctx.closePath();
    ctx.fill();
}
