// ============================================
// CONFIGURATION
// ============================================
let CONFIG = {
    tileSize: 35,              // Tile dimensions (25-50)
    bassRotate: 3.0,           // Bass rotation (2.0-5.0)
    colorSpeed: 0.19,
    gap: 2,                    // Gap between tiles (1-4)
    pattern: "mixed"           // "squares", "circles", "mixed"
};

// ============================================
// MAIN EFFECT
// ============================================

let bass = audio.IsEnabled ? audio.Bass : Math.abs(Math.sin(time * 2)) * 0.7;
let mid = audio.IsEnabled ? audio.Midrange : Math.abs(Math.sin(time * 2.5)) * 0.6;
let treble = audio.IsEnabled ? audio.Treble : Math.abs(Math.sin(time * 3)) * 0.5;

// Colorful background
let bgHue = (time * CONFIG.colorSpeed + 0.6) % 1.0;
let bgRgb = ctx.hslToRgb(bgHue, 0.7, 0.3);
ctx.clear(bgRgb.r, bgRgb.g, bgRgb.b);

let cols = Math.ceil(width / CONFIG.tileSize) + 2;
let rows = Math.ceil(height / CONFIG.tileSize) + 2;

// Draw tiles covering entire canvas
for (let row = -1; row < rows; row++) {
    for (let col = -1; col < cols; col++) {
        let x = col * CONFIG.tileSize + CONFIG.tileSize / 2;
        let y = row * CONFIG.tileSize + CONFIG.tileSize / 2;

        let t = ((row + 1) * cols + (col + 1)) / (rows * cols);

        // Audio value
        let audioValue = t < 0.33 ? bass : (t < 0.66 ? mid : treble);

        // Size and rotation
        let size = CONFIG.tileSize - CONFIG.gap;
        let rotation = time * 0.5 + audioValue * CONFIG.bassRotate + (row + col) * 0.3;

        // Color
        let hue = (t + time * CONFIG.colorSpeed) % 1.0;
        let lightness = 0.4 + audioValue * 0.4;
        let rgb = ctx.hslToRgb(hue, 0.95, lightness);

        // Draw tile
        ctx.save();
        ctx.translate(x, y);
        ctx.rotate(rotation);

        let shape = CONFIG.pattern === "mixed" ? ((row + col) % 2) : (CONFIG.pattern === "circles" ? 1 : 0);

        if (shape === 0) {
            // Square
            ctx.fillStyle(rgb.r, rgb.g, rgb.b);
            ctx.fillRect(-size/2, -size/2, size, size);
        } else {
            // Circle
            ctx.fillStyle(rgb.r, rgb.g, rgb.b);
            ctx.beginPath();
            ctx.arc(0, 0, size/2, 0, Math.PI * 2);
            ctx.fill();
        }

        ctx.restore();
    }
}
