// ═══════════════════════════════════════════════════════════
// CONFIG
// ═══════════════════════════════════════════════════════════
const config = {
  waveSpeed: 2.0,
  segments: 30,
  colorSpeed: 0.5,
  complexity: 3
};

// ═══════════════════════════════════════════════════════════
// RENDER
// ═══════════════════════════════════════════════════════════
ctx.clear(0, 0, 0);

// Draw horizontal gradient strips
for (let i = 0; i < config.segments; i++) {
  let y = (i / config.segments) * height;
  let h = height / config.segments + 1;

  let grad = ctx.createLinearGradient(0, 0, width, 0);

  for (let j = 0; j <= 5; j++) {
    let x = j / 5;
    let wave = Math.sin(x * 6 + i * 0.3 + time * config.waveSpeed) * 0.5 + 0.5;
    let hue = (wave + time * config.colorSpeed) % 1.0;
    let rgb = ctx.hslToRgb(hue, 0.8, 0.5);
    grad.addColorStop(x, rgb.r, rgb.g, rgb.b, 255);
  }

  ctx.fillStyleGradient(grad);
  ctx.fillRect(0, y, width, h);
}
