// ===== CONFIG =====
const gridCols = 16;
const gridRows = 8;
const waveSpeed = 2.0;
const colorSpeed = 0.2;
const minSize = 0.2; // fraction of cell
const maxSize = 0.9; // fraction of cell
// ==================

timeControl.Speed = 0.5 + audio.Volume * 2.5;
ctx.clear(0, 0, 0);

let cellW = width / gridCols;
let cellH = height / gridRows;

for (let x = 0; x < gridCols; x++) {
    for (let y = 0; y < gridRows; y++) {
        // Create wave moving left
        let wavePattern = Math.sin(time * waveSpeed - x * 0.8 + y * 0.3);
        let pulse = wavePattern * 0.5 + 0.5; // normalize to 0-1
        
        // Add audio reactivity
        let index = x + y * gridCols;
        pulse = pulse * 0.7 + audio.GetBand(index % 32) * 0.3;
        
        // Color wave
        let hue = (x / gridCols - time * colorSpeed + y / gridRows * 0.3) % 1.0;
        if (hue < 0) hue += 1.0;
        
        let rgb = ctx.hslToRgb(hue, 1.0, 0.5);
        
        let size = (minSize + pulse * (maxSize - minSize)) * Math.min(cellW, cellH);
        
        ctx.fillStyle(rgb.r, rgb.g, rgb.b);
        ctx.fillCircle(
            x * cellW + cellW/2,
            y * cellH + cellH/2,
            size
        );
    }
}
