// =========================
// CONFIG
// =========================
const STEP = 10;
const ROT_SPEED = 0.5;
const SCALE = 0.02;

// =========================
// RENDER
ctx.clear(0, 0, 0);

const cx = width / 2;
const cy = height / 2;
const timeOffset = time * ROT_SPEED;

// Pre-calculate constants
const step2 = STEP * STEP;

for (let x = 0; x < width; x += STEP) {
    const dx = x - cx;

    for (let y = 0; y < height; y += STEP) {
        const dy = y - cy;

        // Combined calculation
        const angle = Math.atan2(dy, dx) + timeOffset;
        const dist = Math.sqrt(dx*dx + dy*dy);

        const hue = (Math.sin(dist * SCALE - angle) + 1) * 0.5;
        const rgb = ctx.hslToRgb(hue, 1, 0.5);

        ctx.fillStyle(rgb.r, rgb.g, rgb.b, 255);
        ctx.fillRect(x, y, STEP, STEP);
    }
}
