// =========================
// CONFIG
// =========================
const CELL_SIZE = 8;
const BIRTH_THRESHOLD = 0.4;
const DEATH_THRESHOLD = 0.6;
const EVOLUTION_SPEED = 2;
const COLOR_SHIFT = 0.1;

// =========================
// RENDER
ctx.clear(0, 0, 0);

const cols = Math.ceil(width / CELL_SIZE);
const rows = Math.ceil(height / CELL_SIZE);

for (let x = 0; x < cols; x++) {
    for (let y = 0; y < rows; y++) {
        // Pseudo-random cellular pattern
        let cell = Math.sin(x * 7.13 + y * 3.71 + time * EVOLUTION_SPEED);
        let neighbor = Math.sin(x * 5.21 + y * 8.43 + time * EVOLUTION_SPEED * 0.7);

        let isAlive = (cell + neighbor) * 0.5;

        if (isAlive > BIRTH_THRESHOLD - DEATH_THRESHOLD) {
            let age = (isAlive + 1) * 0.5;
            let hue = (age + time * COLOR_SHIFT) % 1;
            let rgb = ctx.hslToRgb(hue, 0.8, 0.5);

            ctx.fillStyle(rgb.r, rgb.g, rgb.b, 255);
            ctx.fillRect(x * CELL_SIZE, y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
        }
    }
}
