// Animated checkerboard with dynamic cell sizes
ctx.clear(0, 0, 0);

const baseSize = 20;

for (let x = 0; x < width; x += baseSize) {
    for (let y = 0; y < height; y += baseSize) {
        // Oscillating size based on position and time
        let sizeModifier = Math.sin(x * 0.05 + time) * 
                          Math.cos(y * 0.05 + time * 1.3) * 0.5 + 0.5;

        let cellSize = baseSize * sizeModifier;

        // Checkerboard pattern with color shift
        let isEven = ((x / baseSize) + (y / baseSize)) % 2;
        let hue = (time * 0.1 + (x + y) / (width + height)) % 1;

        if (isEven < 1) {
            let rgb = ctx.hslToRgb(hue, 1, 0.5);
            ctx.fillStyle(rgb.r, rgb.g, rgb.b, 255);
        } else {
            let rgb = ctx.hslToRgb((hue + 0.5) % 1, 1, 0.3);
            ctx.fillStyle(rgb.r, rgb.g, rgb.b, 255);
        }

        ctx.fillRect(x, y, cellSize, cellSize);
    }
}
