// Organic flowing texture across entire canvas
const STEP = 3;
ctx.clear(0, 0, 0);

for (let x = 0; x < width; x += STEP) {
    for (let y = 0; y < height; y += STEP) {
        // Multi-layered noise pattern
        let noise = Math.sin(x * 0.01 + time) * 
                    Math.cos(y * 0.01 + time * 0.7) +
                    Math.sin((x + y) * 0.005 - time * 2) * 0.5;

        // Map noise to hue and brightness
        let hue = (noise + 2) * 0.16; // 0-1 range
        let brightness = 0.3 + (noise + 2) * 0.1;

        let rgb = ctx.hslToRgb(hue, 0.8, brightness);
        ctx.fillStyle(rgb.r, rgb.g, rgb.b, 255);
        ctx.fillRect(x, y, STEP, STEP);
    }
}
