// Optimized plasma waves - larger blocks, fewer calculations
ctx.clear(0, 0, 0);

// Use larger step to reduce draw calls
const step = 8; // Changed from 4 to 8 (4x fewer draws!)

// Pre-calculate time factors
const t1 = time * 2;
const t2 = time * 3;
const t3 = time;

for (let y = 0; y < height; y += step) {
  const yFactor = y * 0.01;

  for (let x = 0; x < width; x += step) {
    const xFactor = x * 0.01;

    // Combine calculations
    let value = Math.sin(xFactor + t1) + 
                Math.sin(yFactor + t2) + 
                Math.sin((xFactor + yFactor) + t3);

    let r = 128 + 127 * Math.sin(value);
    let g = 128 + 127 * Math.sin(value + 2);
    let b = 128 + 127 * Math.sin(value + 4);

    ctx.fillStyle(r, g, b, 255);
    ctx.fillRect(x, y, step, step);
  }
}
