// =========================
// CONFIG
// =========================
const PULSE_SPEED = 1.5;
const COLOR_BANDS = 8;
const SATURATION = 1.0;
const BRIGHTNESS = 0.6;

// =========================
// RENDER
ctx.clear(255, 240, 250);

const cx = width / 2;
const cy = height / 2;
const pulse = Math.sin(time * PULSE_SPEED) * 0.3 + 1;
const maxRadius = Math.sqrt(width * width + height * height) / 2 * pulse;

const grad = ctx.createRadialGradient(cx, cy, 0, cx, cy, maxRadius);

for (let i = 0; i <= COLOR_BANDS; i++) {
    let hue = (i / COLOR_BANDS + time * 0.15) % 1;
    let rgb = ctx.hslToRgb(hue, SATURATION, BRIGHTNESS);
    grad.addColorStop(i / COLOR_BANDS, rgb.r, rgb.g, rgb.b, 255);
}

ctx.fillStyleGradient(grad);
ctx.fillRect(0, 0, width, height);
