// =========================
// CONFIG
// =========================
const GRID_SIZE = 30;
const WAVE_AMPLITUDE = 15;
const WAVE_FREQUENCY = 0.05;
const TIME_SCALE = 2;
const COLOR_CYCLE = 0.15;

// =========================
// RENDER
ctx.clear(15, 15, 20);

for (let x = 0; x < width; x += GRID_SIZE) {
    for (let y = 0; y < height; y += GRID_SIZE) {
        // Wave distortion
        let waveX = Math.sin(y * WAVE_FREQUENCY + time * TIME_SCALE) * WAVE_AMPLITUDE;
        let waveY = Math.cos(x * WAVE_FREQUENCY + time * TIME_SCALE) * WAVE_AMPLITUDE;

        let centerX = x + GRID_SIZE / 2 + waveX;
        let centerY = y + GRID_SIZE / 2 + waveY;

        // Color based on position
        let hue = ((x + y) / (width + height) + time * COLOR_CYCLE) % 1;
        let rgb = ctx.hslToRgb(hue, 0.9, 0.5);

        ctx.fillStyle(rgb.r, rgb.g, rgb.b, 255);
        ctx.fillCircle(centerX, centerY, GRID_SIZE / 3);
    }
}
