// =========================
// CONFIG
// =========================
const SPIRAL_ARMS = 8;
const ROTATION_SPEED = 1.0;
const SPIRAL_TIGHTNESS = 0.02;
const LINE_WIDTH = 3;
const DEPTH = 200;

// =========================
// RENDER
ctx.clear(0, 0, 0);

const cx = width / 2;
const cy = height / 2;

for (let arm = 0; arm < SPIRAL_ARMS; arm++) {
    let baseAngle = (arm / SPIRAL_ARMS) * Math.PI * 2;
    let hue = (arm / SPIRAL_ARMS + time * 0.1) % 1;
    let rgb = ctx.hslToRgb(hue, 1, 0.5);

    ctx.strokeStyle(rgb.r, rgb.g, rgb.b, 255);
    ctx.lineWidth(LINE_WIDTH);
    ctx.beginPath();

    for (let r = 0; r < DEPTH; r++) {
        let angle = baseAngle + r * SPIRAL_TIGHTNESS + time * ROTATION_SPEED;
        let radius = r * 2;

        let x = cx + Math.cos(angle) * radius;
        let y = cy + Math.sin(angle) * radius;

        if (r === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
    }
    ctx.stroke();
}
